interface props {
    bookTitle: string,
    handleSubmit: Function,
    setBookTitle: Function,
    setDescription: Function
}

const InputSection: React.FC<props> = ({ bookTitle, handleSubmit, setBookTitle, setDescription }) => {

    return (
        <header className="App-header">
            <h1>
                Book Recommender!
            </h1>
            <p>Insert the title of a book you like and/or a description of what you want to see.</p>
            <p>Wait and see the recommendations from it!</p>

            {bookTitle}
            <button onClick={handleFormSubmission}>Click me</button>


            <form onSubmit={handleFormSubmission}>
                <h2>Input Your Information Below</h2>
                <label>
                    Book Title
                    <input type="text" id="query" name="q" onChange={(e: any) => setBookTitle(e.target.value)} />
                </label>
                <div></div>
                <label>
                    Book Description
                    <input type="text" id="query" name="q" onChange={(e: any) => setDescription(e.target.value)} />
                </label>

                <input type="submit" value="Submit" />
            </form>
        </header>
    )
}
