import { AppState } from "../App"
import BookDisplay from "../components/BookDisplay";

interface props {
    appState: AppState,
    recommendations: displayedBookFields[]
}

export interface displayedBookFields {
    title: string;
    author: string;
    rating: number;
    description: string;
}

//book title, author, generated description, rating, cover

const OutputSection: React.FC<props> = ({ appState, recommendations }) => {
    return (
        <div>
            {appState.valueOf() === AppState.Input.valueOf() && 'input'}
            {appState.valueOf() === AppState.Loading.valueOf() && 'loading'}
            {appState.valueOf() === AppState.ModelFinished.valueOf() && 'results found below!'}
            <br></br>
            <br></br>
            <br></br>

            {recommendations.length}
            {
                recommendations.map(rec => {
                    return (
                        <BookDisplay title={rec.title} author={rec.author} rating={rec.rating} description={rec.description} />
                )
                })
            }
        </div>
    )
}

export default OutputSection
