import { useEffect, useState } from "react";
import { AppState } from "../App"
import BookDisplay from "../components/BookDisplay";
import { RotatingLines } from 'react-loader-spinner';
import { Rating } from 'react-simple-star-rating'

interface props {
    appState: AppState,
    recommendations: displayedBookFields[]
}

export interface displayedBookFields {
    title: string;
    author: string;
    rating: number;
    description: string;
    imageUrl: string;
}

//book title, author, generated description, rating, cover

let fillerStatements = ['gathering authorized books...', 'checking with the Ministry of Truth', 'asking your neighbors', 'analyzing recommendation with the Thought Police']

function Loader() {
    const [loadingStatement, setLoadingStatement] = useState(fillerStatements[0])
    const [indexStatement, setIndexStatement] = useState(0);

    function updateLoadingStatement() {
        setIndexStatement((indexStatement + 1) % fillerStatements.length)
        setLoadingStatement(fillerStatements[indexStatement])
    }

    useEffect(() => {
        const interval = setInterval(() => updateLoadingStatement(), 1000);
        return () => {
            clearInterval(interval)
        };
    }, []);

    return (
    <div className="loader">
                <RotatingLines
        visible={true}
        width="96"
        strokeWidth="5"
        animationDuration="0.75"
        ariaLabel="rotating-lines-loading"
        strokeColor="#e76a28"
    />  
    {loadingStatement}...
    </div>
    )
}

const OutputSection: React.FC<props> = ({ appState, recommendations }) => {
    return (
        <div>
            {appState.valueOf() === AppState.Input.valueOf() && 'Follow the steps on the left to get your recommendation.'}
            {appState.valueOf() === AppState.Loading.valueOf() && <Loader />}
            <Loader />
            {
                recommendations.map((rec, index) => {
                    return (
                        <BookDisplay bookFields={rec} index={index + 1} />
                    )
                })
            }
        </div>
    )
}

export default OutputSection
