import React, { ChangeEvent, useState } from 'react';
import './App.css';
import axios from "axios"
function App() {
  const [bookTitle, setBookTitle] = useState("")
  const [description, setDescription] = useState("")

  const handleSubmit = (e: React.FormEvent) => {
    alert('An essay was submitted: ' + bookTitle);
    doKMeans()
    e.preventDefault();
  }

  const [profileData, setProfileData] = useState<ProfileData>({profile_name: "none", about_me: "I don't know"})

  interface ProfileData {
    profile_name: string,
    about_me: string
  }


  function getData() {
    axios({
      method: "GET",
      url:"/profile",
    })
    .then((response) => {
      const res =response.data
      setProfileData(({
        profile_name: res.name,
        about_me: res.about}))
    }).catch((error) => {
      if (error.response) {
        console.log(error.response)
        console.log(error.response.status)
        console.log(error.response.headers)
        }
    })}

    function doKMeans() {
      axios({
        method: "GET",
        url:"/kmeans",
      })
      .then((response) => {
        const res =response.data
        setBookTitle(res)
      }).catch((error) => {
        if (error.response) {
          console.log(error.response)
          console.log(error.response.status)
          console.log(error.response.headers)
          }
      })}

  return (
    <div className="App">
      <header className="App-header">
        {/* new line start*/}
        <p>To get your profile details: </p><button onClick={getData}>Click me</button>
        {profileData && <div>
              <p>Profile name: {profileData.profile_name}</p>
              <p>About me: {profileData.about_me}</p>
            </div>
        }





        <h1>
          Book Recommender!
        </h1>
        <p>Insert the title of a book you like and/or a description of what you want to see.</p>
        <p>Wait and see the recommendations from it!</p>

      <form onSubmit={handleSubmit}>
        <h2>Input Your Information Below</h2>
        <label>
          Book Title
          <input type="text" id="query" name="q" onChange={(e: any) => setBookTitle(e.target.value)}/>
        </label>
        <div></div>
        <label>
          Book Description
          <input type="text" id="query" name="q" onChange={(e: any) => setDescription(e.target.value)}/>
        </label>

        <input type="submit" value="Submit" />
      </form>
      </header>
    </div>
  );
}

export default App;
