import React, { ChangeEvent, useState } from 'react';
import './App.css';
import axios from "axios"
import InputSection from './contentSections/InputSection';
import OutputSection from './contentSections/OutputSection';
import { getBookInformation } from './CallHandler';
import { Border } from './components/Border';

export enum AppState {
  Input, Loading, ModelFinished
}

export interface BookInformation {
  title: string,
  author: string[],
  numInteractions: number,
  averageRating: number,
  numRatings: number,
  description: string,
  numPages: number,
  subjects: string[]
}

function App() {
  const [bookTitle, setBookTitle] = useState("")
  const [description, setDescription] = useState("")
  const [appState, setAppState] = useState(AppState.Input)
  const [recommendations, setRecommendations] = useState<string[]>([])

  const handleSubmit = (e: React.FormEvent) => {
    doKMeans();
    alert('An essay was submitted: ' + bookTitle);

    e.preventDefault();
  }


  function handleSetBookTitle(value: string) {
    setBookTitle(value)
  }

  function handleSetDescription(value: string) {
    setDescription(value)
  }

  function handleSetState(value: AppState) {
    setAppState(value);
  }

  function handleAddRecommendation(value: string) {
    setRecommendations([...recommendations, value])
  }

  function clearRecommendations() {
    setRecommendations([]);
  }

  const doKMeans = async () => {
    let recommendation = '1984'
    if (bookTitle != '') {
      let bookInformation = await getBookInformation(bookTitle) as unknown as BookInformation
      let genres: string[] = []
      let recommendation = ''

      //get the converted genres
      const config = {
        headers: { 'Access-Control-Allow-Origin': '*' }
      };
      await axios
        .post("http://127.0.0.1:5000/convertGenres", {
          input: bookInformation.subjects,
        }, config)
        .then((response) => {
          // console.log(response)
          genres = response.data
        })
        .catch((er) => {
          console.log(er);
        });

      console.log(genres)
      //call the actual kmeans maker function
      // "http://localhost:5000/kmeans"
      axios
        .post("http://127.0.0.1:5000/kmeans", {
          book_genres: genres,
          num_pages: bookInformation.numPages,
          ratings_count: bookInformation.numRatings,
          average_rating: bookInformation.averageRating,
          text_reviews_count: bookInformation.numInteractions
        }, config)
        .then((response) => {
          recommendation = response.data
          console.log(recommendation)
        })
        .catch((er) => {
          console.log(er);
        });
      axios
        .post("http://127.0.0.1:5000/sentenceTransformer", {
          userDescription: description
        }, config)
        .then((response) => {
          recommendation = response.data
          console.log(recommendation)
        })
        .catch((er) => {
          console.log(er);
        });
    }
  }


  return (
    <Border>
      <div className="App">
        <h1>
          Book Recommender
        </h1>
        <h2>
          Praise INGSOC!!! Read What WE Tell You To  :)

        </h2>
        <div className='mainContent'>
          <div className="c1">
            <InputSection bookTitle={bookTitle} handleSubmit={doKMeans} setBookTitle={handleSetBookTitle} setDescription={handleSetDescription} appState={appState} setAppState={handleSetState} />
          </div>
          <div className='c2'>
            <OutputSection appState={appState} setAppState={handleSetState} />
          </div>
        </div>
      </div>
    </Border>
  );
}

export default App;
