import { AppState } from "../App"
import BookDisplay from "../components/BookDisplay";
import Loader from 'react-loader-spinner';
import { Audio } from 'react-loader-spinner'

interface props {
    appState: AppState,
    recommendations: displayedBookFields[]
}

export interface displayedBookFields {
    title: string;
    author: string;
    rating: number;
    description: string;
    imageUrl: string;
}

//book title, author, generated description, rating, cover

const OutputSection: React.FC<props> = ({ appState, recommendations }) => {
    return (
        <div>
            {appState.valueOf() === AppState.Input.valueOf() && 'input'}
            {appState.valueOf() === AppState.Loading.valueOf() && <Audio
  height="80"
  width="80"
  radius="9"
  color="green"
  ariaLabel="three-dots-loading"
/>
            }
            {appState.valueOf() === AppState.Loading.valueOf() || appState.valueOf() === AppState.Loading.valueOf() && <br></br>}

            {
                recommendations.map((rec, index) => {
                    return (
                        <BookDisplay bookFields={rec} index={index + 1} />
                    )
                })
            }
        </div>
    )
}

export default OutputSection
